let url = "https://www.linkedin.com/jobs/search/?f_TPR=r86400"

const keyword = $input.first().json.Keyword
const location =  $input.first().json.Location
const experienceLevel = $input.first().json['Experience Level']
const remote = $input.first().json.Remote
const jobType = $input.first().json['Job Type']
const easyApply = $input.first().json['Easy Apply']

if (keyword != "") {
  url += `&keywords=${keyword}`;
}

if (location != "") {
  url += `&location=${location}`;
}

if (experienceLevel !== "") {
  // we have experienceLevel as a string with values like "Internship, Entry level"
  // we  transform it to "1,2,3" where:
  // Internship -> 1
  // Entry level -> 2
  // Associate -> 3
  // Mid-Senior level -> 4
  // Director -> 5
  // Executive -> 6
  const transformedExperiences = experienceLevel
    .split(",")
    .map((exp) => {
      switch (exp.trim()) {
        case "Internship":
          return "1";
        case "Entry level":
          return "2";
        case "Associate":
          return "3";
        case "Mid-Senior level":
          return "4";
        case "Director":
          return "5";
        case "Executive":
          return "6";
        default:
          return "";
      }
    })
    .filter(Boolean); // filter out any empty strings
  url += `&f_E=${transformedExperiences.join(",")}`;
}

if (remote.length != "") {
  // we have remote as a string with values like "Remote, Hybrid"
  // we transform it to "2,3,1" where:
  // On-Site -> 1
  // Remote -> 2
  // Hybrid -> 3
  const transformedRemote = remote
    .split(",")
    .map((e) => {
      switch (e.trim()) {
        case "Remote":
          return "2";
        case "Hybrid":
          return "3";
        case "On-Site":
          return "1";
        default:
          return "";
      }
    })
    .filter(Boolean); // filter out any empty strings
  url += `&f_WT=${transformedRemote.join(",")}`;
}

if (jobType != "") {
  // we have jobType as a string with values like "Full-time, Part-time"
  // we transform it to "F,P" where:
  // Full-time -> F
  // Part-time -> P
  // Contract -> C
  // Temporary -> T
  // Other -> O
  // Internship -> I
  const transformedJobType = jobType.split(",").map((type) => type.trim().charAt(0).toUpperCase());
  url += `&f_JT=${transformedJobType.join(",")}`;
}

if (easyApply != "") {
  url += "&f_EA=true";
}

return {url}

